import React from "react";

class AddFish extends React.Component {
    name = React.createRef();
    price = React.createRef();
    status = React.createRef();
    desc = React.createRef();
    image = React.createRef();

    createFish = event => {
        event.preventDefault();
        console.log('üê†');
        const fish = {
            name: this.nameRef,
            price: this.priceRef,
            status: this.statusRef,
            desc: this.descRef,
            image: this.imageRef,
        }
        console.log(fish);
    }
    render() {
        return (
                <form className="fish-edit" onSubmit={this.createFish}>
                    <input type="text" placeholder="name" name="name" ref={this.nameRef} />
                    <input type="text" placeholder="price" name="price" ref={this.priceRef}/>
                    <select name="status" ref={this.statusRef}>
                        <option value='available'>Fresh</option>
                        <option value='unavailable'>Sold Out</option>
                    </select>
                    <textarea placeholder="desc" name="desc" ref={this.descRef}/>
                    <input type="text" placeholder="image" name="image" ref={this.imageRef}/>
                    <button type='submit'>+ Add Fish</button>
                </form>
        )
    }
}

export default AddFish;
