import React from "react"; 
import Header from "./Header";
import Inventory from "./Inventory";
import Order from "./Order";
import sampleFishes from '../sample-fishes';
import Fish from './Fish';
import base from '../base'
class App extends React.Component {
    state = {
        fishes: {},
        order: {},
    }

    componentDidMount() {
        // this.ref = base.syncState(`${this.props.match.parms.storeId}`) 
        const { params } = this.props.match;
        this.ref = base.syncState(`${params.storeId}`)    
    }
    addFish = fish => {
        // take copy of exsiting state
        const fishes = {...this.state.fishes}
        // add our data to new 
        fishes[`fish${Date.now()}`] = fish
        // set new object to state
        this.setState({
            fishes: fishes
        })
    }

    loadSample = () => {
        this.setState({ fishes : sampleFishes})
    }

    addToOrder = (key) => {
        // take copy of state
        const order = {...this.state.order}

        // either add to the order, or update the number is our order
        order[key] = order[key] + 1 || 1;

        // call setsState to update our state objective 
        this.setState({order})
    }

    render() {
        return (
            <>
                <React.StrictMode>
                    <div className="catch-of-the-day">
                        <div className="menu">
                            <Header
                                firstPart="Checking"
                                lastPart="Say"
                                tagline="Freash Seafood Market"
                            />
                            <ul className="fishes">
                                {Object.keys(this.state.fishes).map(key=> 
                                <Fish 
                                    key={key} 
                                    indexOf={key}
                                    details={this.state.fishes[key]} 
                                    addToOrder={this.addToOrder}
                                    />)}
                                {/* this loops over each fish based on key*/}
                            </ul>
                        </div>
                        <Order fishes={this.state.fishes} order={this.state.order}/>
                        <Inventory addFish={this.addFish} loadSample={this.loadSample}/>
                    </div>
                </React.StrictMode>
            </>
        )
    }
}

export default App
